            Loading,
            ViewBox,
            XHeader,
            Divider,
            Group,
            Cell,
            Switch,
            Actionsheet,
            Confirm
        },
        props: {
            isResult:Boolean,
        },

        store: store,
        vuex: {
            getters: {
                route: (state) => state.route,
                isLoading: (state) => state.isLoading,
                direction: (state) => state.direction
            }
        },

        data (){
            return {
                isPageShow:true,//显示页面
            }
        },
        methods: {

            //返回顶部 START
            scrollTop () {
                this.$refs.viewBox.$els.viewBoxBody.scrollTop = 0
            }
            //返回顶部 END

        },

        computed: {
            leftOptions () {
                return {
                    showBack: true
                }
            },
            rightOptions: {
                type: Object,
                default () {
                    return {
                        showMore: false
                    }
                }
            },
            headerTransition () {
                return this.direction === 'forward' ? 'ay-header-fade-in-right' : 'ay-header-fade-in-left'
            },

            title () {
                if (this.route.path.indexOf('/step') >=0) 
                {
                    return '签证状态进度'
                }
                else
                {
                    return '我的签证状态'
                }
            }
        },

        ready(){
//            this.takeData()
        }
    }

